mapper.select:
SELECT 

FROM chapter a

WHERE a.id_ = #{id}

SELECT 

FROM chapter a




		AND a.name_ LIKE 
			'%'||#{name}||'%'
			'%'+#{name}+'%'
			concat('%',#{name},'%')


		AND a.status_ = #{status}


		AND a.parent_id_ = #{parentId}


		and a.sort_ = #{sort}




		ORDER BY ${page.orderBy}


		ORDER BY a.sort_ asc

SELECT 

FROM chapter a






		ORDER BY ${page.orderBy}
